const menuToggle=document.querySelector(".menu-toggle"),nav=document.querySelector("nav"),navLinks=document.querySelectorAll("nav li a");function toggleNav(){nav.classList.toggle("active"),menuToggle.classList.toggle("active")}function closeMenu(){menuToggle.classList.remove("active"),nav.classList.remove("active")}navLinks.forEach(e=>e.addEventListener("click",closeMenu)),menuToggle.addEventListener("click",toggleNav);